from flask import Flaskfrom flask import render_templateposX = 0posY = 0app = Flask(__name__, static_url_path='')print('Version 4 - Sogyo.nl Raspberry Pi webserver')print('Setting up RPi server...')@app.route('/')def index():    return app.send_static_file('index.html')@app.route('/param=<route>')def parameters(route):    return 'Taking parameters'@app.route('/move=<direction>')def move(direction):    return direction@app.route('/forward')def forw():    import motor_control.py    forward(10)    return 'Moving forward'@app.route('backward')def backw():    import motor_control.py    backward(10)    return 'Moving backward'@app.route('/location')def location():    return 'Location: ' + str(posX) + ', ' + str(posY)if __name__ == '__main__':    print('Ready.')    app.run(debug=True, host='0.0.0.0')